<?php/*Plugin Name: Simple Wordpress to Mindtalk PosterPlugin URI: https://www.github.com/at-/php/wp-to-mindtalk.phpDescription: See plugin nameVersion: 0.1Author: at-Author URI: https://www.github.com/at-License: WTFPL (What The Fuck Public License) v6.9*/include_once( ABSPATH . 'wp-admin/includes/plugin.php' ); // required in order to access is_plugin_active()function mtcall() {	$url = 'http://api.mindtalk.com/v1/post/write_mind';	$token = '[YOUR TOKEN]';	$id = '[UID/CID]'; // Support uid, channel id, etc (read mindtalk docs)	if ( is_plugin_active('jetpack/jetpack.php') ) {		$body = 'access_token=' . $token . '&message='.get_the_title() .' '.wp_get_short_link().'&origin_id=' . $id . '&rf=json';	} else {		$body = 'access_token=' . $token . '&message='.get_the_title() .' '.get_permalink().'&origin_id=' . $id . '&rf=json';	}	$c = curl_init ($url);	curl_setopt ($c, CURLOPT_POST, true);	curl_setopt ($c, CURLOPT_POSTFIELDS, $body);	curl_setopt ($c, CURLOPT_RETURNTRANSFER, true);	$page = curl_exec ($c);	curl_close ($c);}add_action( 'draft_to_publish', 'mtcall' );?>